import React from "react";
import '../style/article.css'

export class Article extends React.Component {
    constructor (props) {
        super (props)
        this.state = {

        }
    }

    componentDidMount () {
        const vditor =  new vditor('vditor', {
            height: 360,
            toolbarConfig: {
                pin: true,
            },
            cache: {
                enable: false,
            },
            after () {
                vditor.setValue('Hello, Vditor + React!')
            },
        })
    }

    render () {
        return (
            <article>
                <div id='vditor'>

                </div>
            </article>
        )
    }
}
