import React from "react";
import Vditor from 'vditor';
import styles from '../style/chat.module.css'

export default class Chat extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            editor: null

        }
    }

    componentDidMount() {
        const that = this
        this.state.editor = new Vditor('chatMenu', {
            "height": 360,
            "width":"100%",
            "theme":'dark',
            "preview": {
                "theme": {
                    "current": "dark"
                }
            },
            "cache": {
                "enable": false,
            },
            "toolbarConfig": {
                hide: false,
            },
            after () {
                that.state.editor.setValue('')
            },
        })
    }

    render () {
        return (
            <div className={styles.chatMenu}>
                {/* 聊天输入框 */}
                <div id='chatMenu'>
                </div>
                {/* 提交数据 */}
                {/* 历史记录显示框 */}
                <div className={styles.chatShow}>
                    <ChatLists></ChatLists>
                </div>
            </div>
        )
    }
}

function ChatLists(props) {
    return (
        <div>
            <pre>
            It's the list!\n
            sd\n
            sase\n
            wqeqwew
            qwew
            </pre>
        </div>
    )
}
