import React from "react";
import Vditor from 'vditor';
import styles from '../style/chat.module.css'

export default class Chat extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            editor: null

        }
    }

    componentDidMount() {
        this.state.editor = new Vditor('chatMenu', {
            "height": 360,
            "width":"80%",
            "theme":'dark',
            "preview": {
                "theme": {
                    "current": "dark"
                }
            },
            "cache": {
                "enable": false,
            },
            "toolbarConfig": {
                hide: false,
            },
            after () {
                this.state.editor.setValue('')
            },
        })
    }

    render () {
        return (
            <div >
                {/* 聊天输入框 */}
                <div id='chatMenu'>

                </div>
                {/* 历史记录显示框 */}
                <div>

                </div>
            </div>
        )
    }
}
