import React from "react";
import '../style/article-list.css'
import {RedoOutlined} from '@ant-design/icons'
import { Select } from 'antd';

export class ArticleList extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            header: '文章列表',
            index: [
                {
                    page: '文章内容1',
                    time: '2020-3-1'
                },
                {
                    page: '文章内容2',
                    time: '2020-3-2'
                },
                {
                    page: '文章内容3',
                    time: '2020-3-3'
                },

            ],
            selectNode: [
                {
                    name: '列表1',
                    value: '值1'
                },
                {
                    name: '列表2',
                    value: '值2'
                },
                {
                    name: '列表3',
                    value: '值3'
                }
            ]
        }
    }

    render () {
        return (
            <nav className='nav-list'>
                <header className='header-name'>
                    <span>{this.state.header}</span>
                </header>
                <main className='main-list'>
                    <Search props={this.state.selectNode} />
                    {this.state.index.map((node, index) => 
                        <article className='article-node' key={index}>
                            <span className="article-node-page">{node.page}</span>
                            <span className="article-node-time">{node.time}</span>
                        </article>
                    )}
                </main>
            </nav>
        )
    }
}

function Search (props) {
    const { Option } = Select;

    return (
        <div className='search-container'>
            <div className='search'>
                <input placeholder='搜索文章' className='search-article'></input>
                <div className='refresh'>
                    <RedoOutlined  />
                </div>
            </div>
            <div className='select'>
                <div>
                    <Select>
                        {props.articleType.map((node, index) => <Option key={index} value={node['value']}>{node['name']}</Option>)}
                    </Select>
                </div>
                <div></div>
            </div>
        </div>
    )
}
