import 'vditor/src/assets/scss/index.scss';

export class Article extends React.Component {
    constructor (props) {
        super (props)
        this.state = {

        }
    }

    componentDidMount () {
        const vditor =  new Vditor('vditor', {
            "height": 360,
            "width":"80%",
            "theme":'dark',
            "preview": {
                "theme": {
                    "current": "dark"
                }
            },
            "cache": {
                "enable": false,
            },
            "toolbarConfig": {hide: false},
            // after () {
