        // document.getElementsByTagName('pre')?.forEach(node => node.setAttribute('contenteditable', 'false'))
        // setInterval(() => {console.log(vditor.getValue())}, 5000)
    }

    render () {
        return (
            <article className='vditor-container'>
                <div id='vditor' className='vditor'>
                </div>
            </article>
        )
    }
}
