import React from "react";
import '../style/article-list.css'
import {RedoOutlined} from '@ant-design/icons'
import { Select } from 'antd';
import 'antd/dist/antd.css';

export class ArticleList extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            header: '文章列表',
            index: [
                {
                    page: '文章内容1',
                    time: '2020-3-1'
                },
                {
                    page: '文章内容2',
                    time: '2020-3-2'
                },
                {
                    page: '文章内容3',
                    time: '2020-3-3'
                },

            ],
            selectNode: [
                {
                    name: '列表1',
                    value: '值1'
                },
                {
                    name: '列表2',
                    value: '值2'
                },
                {
                    name: '列表3',
                    value: '值3'
                }
            ]
        }
    }

    render () {
        return (
            <nav className='nav-list'>
                <header className='header-name'>
                    <span>{this.state.header}</span>
                </header>
                <main className='main-list'>
                    <Search articleType={this.state.selectNode} />
                    {this.state.index.map((node, index) => 
                        <article className='article-node' key={index}>
                            <span className="article-node-page">{node.page}</span>
                            <span className="article-node-time">{node.time}</span>
                        </article>
                    )}
                </main>
            </nav>
        )
    }
}

function Search (props) {
    const { Option } = Select;

    return (
        <div className='search-container'>
            <div className='search'>
                <input placeholder='搜索文章' className='search-article'></input>
                <div className='refresh'>
                    <RedoOutlined  />
                </div>
            </div>
            <div className='select'>
                <div>
                <Select
    showSearch
    style={{ width: 200 }}
    placeholder="Search to Select"
    optionFilterProp="children"
    filterOption={(input, option) =>
      option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
    }
    filterSort={(optionA, optionB) =>
      optionA.children.toLowerCase().localeCompare(optionB.children.toLowerCase())
    }
  >
    <Option value="1">Not Identified</Option>
    <Option value="2">Closed</Option>
    <Option value="3">Communicated</Option>
    <Option value="4">Identified</Option>
    <Option value="5">Resolved</Option>
    <Option value="6">Cancelled</Option>
  </Select>
                </div>
                <div></div>
            </div>
        </div>
    )
}
