import React from "react";
import '../style/article-list.css'
import {RedoOutlined} from '@ant-design/icons'
import { Select } from 'antd';
import 'antd/dist/antd.css';

export class ArticleList extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            header: '文章列表',
            index: [
                {
                    page: '文章内容1',
                    time: '2020-3-1'
                },
                {
                    page: '文章内容2',
                    time: '2020-3-2'
                },
                {
                    page: '文章内容3',
                    time: '2020-3-3'
                },

            ],
            selectNode: [
                {
                    name: '列表1',
                    value: '值1'
                },
                {
                    name: '列表2',
                    value: '值2'
                },
                {
                    name: '列表3',
                    value: '值3'
                }
            ]
        }
    }

    render () {
        return (
            <nav className='nav-list'>
                <header className='header-name'>
                    <span>{this.state.header}</span>
                </header>
                <main className='main-list'>
                    <Search articleType={this.state.selectNode} handleChange={this.handleChange} />
                    {this.state.index.map((node, index) => 
                        <article className='article-node' key={index}>
                            <span className="article-node-page">{node.page}</span>
                            <span className="article-node-time">{node.time}</span>
                        </article>
                    )}
                </main>
            </nav>
        )
    }

    handleChange(value) {
        console.log(value)
    }
}

function Search (props) {
    const { Option } = Select;

    return (
        <div className='search-container'>
            <div className='search'>
                <input placeholder='搜索文章' className='search-article'></input>
                <div className='refresh'>
                    <RedoOutlined  />
                </div>
            </div>
            <div className='select'>
                <div className='select-name'>
                    <Select
                        showSearch
                        style={{ width: 500 }}
                        placeholder="类型分类"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                        option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                        filterSort={(optionA, optionB) =>
                        optionA.children.toLowerCase().localeCompare(optionB.children.toLowerCase())
                        }
                    >
                        {props.articleType.map((node, index) => <Option key={index} value={node['value']}>{node['name']}</Option>)}
                    </Select>
                </div>
                <div>
                        <Select
                            mode='tags'
                            placeholder="选择标签分类"
                            onChange={props.handleChange}
                        >
                            
                        </Select>
                </div>
            </div>
        </div>
    )
}
